import React, { Component } from 'react'

class Square extends Component{

  render(){
    let isThereBattleShip = this.props.battleships[this.props.index]
    return(
      <React.Fragment>
        <div
          id="square"
          style = { {backgroundColor: this.props.grid[this.props.index]} }
          onClick = {() => this.props.handleChange(this.props.index)}
        >

        </div>
      </React.Fragment>
    )
  }
}
export default Square
